import { createAction, handleActions } from "redux-actions";
import { produce } from "immer";

import axios from "axios";

const SET_POST = "SET_POST"; // 리덕스에 데이터 넣어주는 애
const ADD_POST = "ADD_POST";

const setPost = createAction(SET_POST, (post_list) => ({ post_list }));
const addPost = createAction(ADD_POST, (post) => ({ post }));

const initialState = {
  list: [],
};

const initialPost = {
  id: 0,
  title: "실내 자전거",
  img:
    "https://dnvefa72aowie.cloudfront.net/origin/article/202104/bce02f7c25d41ea2f758c3322de4596b9768b7fc8822ed6ab1a184fd232c50ce.webp?q=82&s=300x300&t=crop",
  price: "10,000원",
  region: "경기도 용인시 수지구 죽전동",
  like: "관심:3",
  chat: "채팅:5",
};

const SetPostDB = (id, title, img, price, region, like, chat) => {
  return function (dispatch, getState, { history }) {
    axios({
      method: "get",
      url: "https://606b2fbdf8678400172e5d01.mockapi.io/articles",
      date: {
        id: id,
        title: title,
        img: img,
        price: price,
        region: region,
        like: like,
        chat: chat,
      },
    }).then((res) => {
      dispatch(
        setPost({
          id: id,
          title: title,
          img: img,
          price: price,
          region: region,
          like: like,
          chat: chat,
        })
      );
    });
  };
};

export default handleActions(
  {
    [SET_POST]: (state, action) => produce(state, (draft) => {}),
    [ADD_POST]: (state, action) => produce(state, (draft) => {}),
  },
  initialState
);

const actionCreators = {
  setPost,
  addPost,
};

export { actionCreators };
