import React from "react";

import styled from "styled-components";
import banner3 from "../shared/banner3.png";

const Banner3 = () => {
  return (
    <React.Fragment>
      <div style={{ padding: "6rem 0" }}>
        <Div>
          <Image></Image>
          <div>
            <H1>
              이웃과 함께 하는 <br /> 동네 생활
            </H1>
            <P>우리 동네의 다양한 이야기를 이웃과 함께 나누어요.</P>
            <ul>
              <li>
                <div className="icon1">1</div>
                <div className="boldtext1"></div>
                <div className="text1"></div>
              </li>
              <li>
                <div className="icon2">2</div>
                <div className="boldtext2"></div>
                <div className="text2"></div>
              </li>
              <li>
                <div className="icon3">3</div>
                <div className="boldtext3"></div>
                <div className="text3"></div>
              </li>
            </ul>
          </div>
        </Div>
      </div>
    </React.Fragment>
  );
};

export default Banner3;

const Div = styled.div`
  display: flex;
  flex-direction: row-reverse;
  align-items: center;
`;

const Image = styled.div`
  background-image: url(${banner3});
  background-size: 546px 740px;
  width: 546px;
  height: 740px;
`;

const H1 = styled.div`
  font-size: 4rem;
  line-height: 1.35;
  margin-bottom: 2.4rem;
`;

const P = styled.p`
  font-size: 17px !important;
  line-height: 1.5;
  letter-spacing: -0.3px;
`;
