import React from "react";
import Image from "../elements/Image";
import Text from "../elements/Text";
import styled from "styled-components";

const PostDetail = (props) => {
  return (
    <React.Fragment>
      <div style={{ margin: "0px 480px 0px 480px" }}>
        <p>
          <img
            style={{
              display: "block",
              margin: "auto",
              marginTop: "50px",
              borderRadius: "10px",
              width: "600px",
              height: "400px",
            }}
            src={props.img}
          />
        </p>

        <div style={{ margin: "0px 170px 0px 170px" }}>
          <p style={{ marginTop: "30px" }}>{props.nickname}</p>
          <Image shape="circle" src={props.img} />
          <p> {props.region}</p>
          <hr />
        </div>

        <div style={{ margin: "0px 170px 0px 170px" }}>
          <Text bold>{props.title}</Text>
          <p>{props.category}</p>
          <Text bold>{props.price}</Text>
          <p>{props.contents}</p>
          <p>
            {props.like} {props.chat} {props.viewNum}
          </p>
        </div>
      </div>
    </React.Fragment>
  );
};

PostDetail.defaultProps = {
  img:
    "https://dnvefa72aowie.cloudfront.net/origin/article/202104/bce02f7c25d41ea2f758c3322de4596b9768b7fc8822ed6ab1a184fd232c50ce.webp?q=82&s=300x300&t=crop",
  nickname: "오창희",
  region: "경기도 용인시 수지구 죽전동",
  title: "실내 자전거",
  category: "가구/인테리어 ",
  price: "10,000원",
  contents:
    "구매한지 2개 월 남짓 되었고, 잠깐 사용해서 깨끗합니다. 총 3개인데, 개별 거래도 가능하고 3개 모두 사시는 분께는 할인해 드리겠습니다.",
  like: "관심 3",
  chat: "채팅 5",
  viewNum: "조회 10",
};

const dIV = styled.div`
  width: 1500px;
  height: 760px;
  margin: 0px 100px 0px 200px;
`;

export default PostDetail;
