import React from "react";
import { Grid } from "../elements";
import styled from "styled-components";

const Header = (props) => {
  return (
    <React.Fragment>
      <Grid is_flex padding="5px 20px" width="100%">
        <Image src="https://d1unjqcospf8gs.cloudfront.net/assets/home/base/header/logo-basic-24b18257ac4ef693c02233bf21e9cb7ecbf43ebd8d5b40c24d99e14094a44c81.svg"></Image>
        <InputWrap>
          <Grid is_flex>
            <Input placeholder="동네 이름, 물품명 등을 검색해보세요!"></Input>
            <img src="https://d1unjqcospf8gs.cloudfront.net/assets/home/base/header/search-icon-7008edd4f9aaa32188f55e65258f1c1905d7a9d1a3ca2a07ae809b5535380f14.svg"></img>
          </Grid>
        </InputWrap>
        <Grid is_flex margin="auto" width="300px">
          <a href="https://apps.apple.com/kr/app/pangyojangteo/id1018769995?l=ko&ls=1">
            <img src="https://d1unjqcospf8gs.cloudfront.net/assets/home/base/header/apple-store-3a664174124650d63cae365bc55586fc5ff27b822b1b97788fc4af77d73d00c8.svg"></img>
            <Button>App Store</Button>
          </a>
          <a href="https://play.google.com/store/apps/details?id=com.towneers.www">
            <Button>Google Play</Button>
          </a>
        </Grid>
      </Grid>
    </React.Fragment>
  );
};

Header.defaultProps = {};

export default Header;

const InputWrap = styled.div`
  border-radius: 5px;
  border: solid 1px #e9ecef;
  text-decoration: none;
  padding: 0 1.6rem;
  height: 40px;
  box-sizing: border-box;
  margin: 10px 20px;
`;

const Input = styled.input`
  border: hidden;
  margin: 0px;
  padding: 10px;
  width: 340px;
  font-size: 16px;
  font-weight: 500;
  margin: auto;
`;

const Image = styled.img`
  width: 120px;
  vertical-align: text-bottom;
  margin: auto;
`;

const Button = styled.div`
  display: inline-block;
  color: #495057;
  font-size: 14px;
  margin-left: 8px;
  font-weight: 700;
  vertical-align: middle;
`;
