import React from "react";
import styled from "styled-components";

import Post from "../components/Post";
import { useDispatch, useSelector } from "react-redux";

import { actionCreators as postActions } from "../redux/modules/post";

const PostDetail = (props) => {
  const dispatch = useDispatch();
  const post_list = useSelector((state) => state.post.list);

  React.useEffect(() => {
    dispatch(postActions.getPostDB());
  }, []);

  return (
    <React.Fragment>
      <Div>
        <Image src={props.imgs}></Image>

        <Profile>
          <Icon src="https://d1unjqcospf8gs.cloudfront.net/assets/users/default_profile_80-7e50c459a71e0e88c474406a45bbbdce8a3bf2ed4f2efcae59a064e39ea9ff30.png"></Icon>
          <div>
            <Nickname>{props.nickname}</Nickname>
            <div style={{ fontSize: "14px" }}>{props.region}</div>
          </div>
        </Profile>

        <div>
          <Title>{props.title}</Title>
          <Category>{props.category}</Category>
          <Price>{props.price}</Price>
          <Contents>{props.contents}</Contents>
          <div style={{ color: "gray" }}>
            {props.chat} ∙ {props.like} ∙ {props.viewNum}
          </div>
        </div>

        <Poststyle>
          {post_list.map((p, idx) => {
            return <Post key={p.id} {...p} />;
          })}
        </Poststyle>
      </Div>
    </React.Fragment>
  );
};

PostDetail.defaultProps = {
  id: 0,
  imgs:
    "https://dnvefa72aowie.cloudfront.net/origin/article/202104/bce02f7c25d41ea2f758c3322de4596b9768b7fc8822ed6ab1a184fd232c50ce.webp?q=82&s=300x300&t=crop",
  nickname: "오창희",
  region: "경기도 용인시 수지구 죽전동",
  title: "실내 자전거",
  category: "가구/인테리어 ",
  price: "10,000원",
  contents:
    "구매한지 2개 월 남짓 되었고, 잠깐 사용해서 깨끗합니다. 총 3개인데, 개별 거래도 가능하고 3개 모두 사시는 분께는 할인해 드리겠습니다.",
  like: "관심 3",
  chat: "채팅 5",
  viewNum: "조회 10",
};

export default PostDetail;

const Div = styled.div`
  margin-top: 100px;
  padding-bottom: 0;
  display: flex;
  flex-direction: column;
  align-items: center;
  margin: 0px auto;
`;

const Image = styled.img`
  width: 680px;
  height: 500px;
  background-image: url("https://dnvefa72aowie.cloudfront.net/origin/article/202104/bce02f7c25d41ea2f758c3322de4596b9768b7fc8822ed6ab1a184fd232c50ce.webp?q=82&s=300x300&t=crop");
  background-size: 680px 680px;
  border-radius: 12px;
  border: 0px solid transparent;
`;

const Nickname = styled.div`
  width: 680px;
  margin: 0px auto;
  font-size: 17px;
  font-weight: 900;
  display: flex;
  align-items: flex-start;
  margin-top: 5px;
`;

const Icon = styled.img`
  width: 40px;
  height: 40px;
  -o-object-fit: cover;
  object-fit: cover;
  border-radius: 50%;
  -webkit-border-radius: 50%;
  -moz-border-radius: 50%;
  display: flex;
  justify-content: flex-start;
  margin-right: 5px;
`;

const Profile = styled.div`
  display: flex;
  justify-content: flex-start;
  width: 680px;
  margin: 0px auto;
  margin-top: 20px;
  border-bottom: 1px solid #e9ecef;
  padding-bottom: 15px;
`;

const Title = styled.div`
  width: 680px;
  margin: 0px auto;
  font-size: 20px;
  font-weight: 900;
  display: flex;
  align-items: flex-start;
  margin-top: 30px;
`;

const Category = styled.div`
  color: gray;
  margin: 0px auto;
  margin-top: 7px;
  margin-bottom: 7px;
  width: 680px;
`;

const Price = styled.div`
  margin: 0px auto;
  margin-top: 7px;
  width: 680px;
  font-weight: 900;
  font-size: 18px;
  margin-bottom: 20px;
`;

const Contents = styled.div`
  margin: 0px auto;
  margin-top: 7px;
  margin-bottom: 20px;
  width: 680px;
  font-size: 18px;
  line-height: 1.6;
  letter-spacing: -0.6px;
  font-weight: 500;
  border-bottom: 1px solid #e9ecef;
`;

const Poststyle = styled.div`
  width: 680px;
  display: flex;
  justify-content: space-around;
  flex-wrap: wrap;
  margin: 0px auto;
`;
